import './App.css';
import {useRef, useState} from "react";
import Preview from "./Preview";
import ImageModal from "./ImageModal";

// import {renderToStaticMarkup} from "react-dom/server";

function App () {
  const [editTab, setEditTab] = useState(true);
  const [textCopied, setTextCopied] = useState(false);
  const [userDetails, setUserDetails] = useState({
    firstName: 'Firstname',
    lastName: 'Lastname',
    designation: 'Designation',
    phoneNumber: '+91 111 111 1111',
    linkedInUsername: 'LinkedInUsername',
    linkedInURL: 'https://www.linkedin.com/company/smarthub-ai',
    photo: 'data:image/png;base64,'
  });
  const [showModal, setShowModal] = useState(false);
  const previewRef = useRef();

  const handleChange = (e, property) => {
    setTextCopied(false);
    const data = {
      [property]: e.target.value
    };
    setUserDetails((state) => {
      return { ...state, ...data };
    });
  }

  const onPhotoAdded = (photo) => {
    const data = { photo }
    setUserDetails((state) => {
      return { ...state, ...data };
    });
    setShowModal(false);
  };

  const onClickNavItem = () => {
    setEditTab((isActive) => {
      return !isActive;
    });
  }

  const onCopiedToClipBoard = () => {
    setTextCopied(true);
  }

  const closeModal = () => {
    console.log('closeModal');
    setShowModal(false);
  }

  return (
    <div className="App">
      <div className="container-wrap">
        <div className="container">
          <nav className="es-nav">
            <ul>
              <li className={editTab ? 'active' : ''}>
                <button onClick={onClickNavItem} className="es-nav-item">Edit</button>
              </li>
              <li className={editTab ? '' : 'active'}>
                <button onClick={onClickNavItem} className="es-nav-item">Preview</button>
              </li>
            </ul>
          </nav>
          <main>
            {editTab ? (
              <div className="edit-wrapper">
                <div className="profile-wrap">
                  <div className="profile-img">
                    <img src={userDetails.photo} alt=""/>
                  </div>
                  <div className="profile-btn">
                    <button
                      onClick={() => setShowModal(true)}
                      className="btn btn-profile" type="button">
                      Change Photo
                    </button>
                  </div>
                </div>
                <form action="">
                  <div className="form-control">
                    <label htmlFor="firstName">First Name</label>
                    <input type="text" value={userDetails.firstName} id="firstName"
                           onChange={(e) => handleChange(e, 'firstName')}/>
                  </div>
                  <div className="form-control">
                    <label htmlFor="lastName">Last Name</label>
                    <input type="text" value={userDetails.lastName} id="lastName"
                           onChange={(e) => handleChange(e, 'lastName')}/>
                  </div>
                  <div className="form-control">
                    <label htmlFor="designation">Designation</label>
                    <input type="text" value={userDetails.designation} id="designation"
                           onChange={(e) => handleChange(e, 'designation')}/>
                  </div>
                  <div className="form-control">
                    <label htmlFor="phoneNumber">Phone Number</label>
                    <input type="phone" value={userDetails.phoneNumber} id="phoneNumber"
                           onChange={(e) => handleChange(e, 'phoneNumber')}/>
                  </div>
                  <div className="form-control">
                    <label htmlFor="linkedInUsername">LinkedIn Username</label>
                    <input type="text" value={userDetails.linkedInUsername} id="linkedInUsername"
                           onChange={(e) => handleChange(e, 'linkedInUsername')}/>
                  </div>
                  <div className="form-control">
                    <label htmlFor="linkedInURL">LinkedIn Link</label>
                    <input type="text" value={userDetails.linkedInURL} id="linkedInURL"
                           onChange={(e) => handleChange(e, 'linkedInURL')}/>
                  </div>
                  <div className="btn-wrap">
                    <button className="btn btn-preview" type="button"
                            onClick={() => setEditTab(false)}>
                      Preview
                    </button>
                  </div>
                </form>
              </div>
            ) : (
              <div className="preview wrapper">
                <Preview ref={previewRef} userDetails={userDetails} onCopiedToClipBoard={onCopiedToClipBoard}/>
              </div>
            )}
          </main>
          {editTab ? '' : (<div className="btn-wrap btn-download-wrap">
            <button className="btn btn-download" type="button"
                    onClick={() => previewRef.current.copyCode()}>
              {textCopied ? 'HTML Code Copied to Clipboard' : 'Copy Code'}
            </button>
          </div>)}
        </div>
      </div>
      <div className={'modal-wrap ' + (showModal && 'active')}>
        <ImageModal onPhotoAdded={onPhotoAdded} closeModal={closeModal}/>
      </div>
    </div>
  );
}

export default App;
